<?phpinclude 'global.php';if(empty($_GET['oauth_token']))	header('location:index.php');	if(!isset($_SESSION['linkedin_user_id'])){	$obj->setToken($_GET['oauth_token'],$_SESSION['oauth_token_secret']);	$token = $obj->getAccessToken();	$_SESSION['oauth_token'] = $token->oauth_token;	$_SESSION['oauth_token_secret'] = $token->oauth_token_secret;}$obj->setToken($_SESSION['oauth_token'], $_SESSION['oauth_token_secret']);$api_url = "http://api.linkedin.com/v1/people/~:(id,first-name,last-name)";$info = $obj->httpRequest('GET',$api_url, null);$xml = simplexml_load_string($info->data);print_r($xml);$id = $xml->id;$first = 'first-name';$last = 'last-name';$first = $xml->$first;$last = $xml->$last;$_SESSION['linkedin_user_id'] = (string)$id;$_SESSION['firstname'] = (string)$first;$_SESSION['lastname'] = (string)$last;$check = $db->get_num_row('user', "userId='$id'");if($check > 0){	$data = array('token'=>$_SESSION['oauth_token'],'tokenSecret'=>$_SESSION['oauth_token_secret']);	$db->save_data('user' , $data , "userId='$id'");}else{	$data = array('userId'=>$id,'token'=>$_SESSION['oauth_token'],'tokenSecret'=>$_SESSION['oauth_token_secret']);	$db->save_data('user' , $data);}header("location:index.php");?>