<?phpinclude_once('global.php');if(!isset($_SESSION['oauth_token']) OR !isset($_SESSION['oauth_token_secret']) OR !isset($_SESSION['linkedin_user_id'])){	$users = $db->get_single_data('user' , "id='0'");		$_SESSION['oauth_token'] = $users['token'];	$_SESSION['oauth_token_secret'] = $users['tokenSecret'];	$_SESSION['linkedin_user_id'] = $users['userId'];}$obj->setToken($_SESSION['oauth_token'], $_SESSION['oauth_token_secret']);$lastMember = array();echo "Start processing<br /><br />";// Delete Current Date First$now = date('Y-m-d');$db->delete_data('connection',"DATE(dateCreated) = '$now'");//Save Data Profile (personal)$userid = $_SESSION['linkedin_user_id'];if($db->get_num_row('connection',"userId='$userid' AND DATE(dateCreated) = '$now'") == 0){	$xml = saveProfile($_SESSION['linkedin_user_id'],'','0',99);}$lastMember[] = $_SESSION['linkedin_user_id'];echo "Save personal profile complete<br /><br />";//Load 1st Level$xml = loadLevel($_SESSION['linkedin_user_id']);$level1 = idArray($xml);$level1 = cleanID($level1, $lastMember);//Save 1st Level to databaseforeach($level1 as $userid){	if($db->get_num_row('connection',"userId='$userid' AND DATE(dateCreated) = '$now'") == 0){		saveProfile($userid,'',$_SESSION['linkedin_user_id'],1);	}}echo "Save 1st level connection profile complete<br /><br />";// Jeda 10 detik//sleep(10);//Combine with last array member$lastMember = array_merge($lastMember, $level1);// Load data level 2$level2 = array();foreach($level1 as $l1){	$xml = loadLevel($l1,'2');	foreach($xml->person as $p){		$userid = (string)$p->id;		$level2[] = $userid;		if($db->get_num_row('connection',"userId='$userid' AND DATE(dateCreated) = '$now'") == 0){			saveProfile($userid,'',$l1,2);		}	}}echo "Save 2nd level connection profile complete<br /><br />";$level2 = array_unique($level2);$level2 = cleanID($level2, $lastMember);$lastMember = array_merge($lastMember, $level2);// Jeda 10 detik//sleep(10);//Load data level 3$level3 = array();foreach($level2 as $l2){	$xml = loadLevel($l2,'3');	foreach($xml->person as $p){		$userid = (string)$p->id;		$level3[] = $userid;		if($db->get_num_row('connection',"userId='$userid' AND DATE(dateCreated) = '$now'") == 0){			saveProfile($userid,'',$l2,3);		}	}}echo "Save 3rd level connection profile complete<br /><br />COMPLETE ALL";$level3 = array_unique($level2);$level3 = cleanID($level3, $lastMember);?>